# === –í–æ–ø—Ä–æ—Å—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é ===
questions = [
    {"question": "–ö–∞–∫–æ–π —É –≤–∞—Å —Ç–∏–ø –∫–æ–∂–∏?", "options": ["—Å—É—Ö–∞—è", "–∂–∏—Ä–Ω–∞—è", "–∫–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–∞—è", "—á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–∞—è", "–Ω–æ—Ä–º–∞–ª—å–Ω–∞—è"], "key": "skin_type"},
    {"question": "–ö–∞–∫–∏–µ –ø—Ä–æ–±–ª–µ–º—ã –∫–æ–∂–∏ –≤–∞—Å –±–µ—Å–ø–æ–∫–æ—è—Ç?", "options": ["—Å—É—Ö–æ—Å—Ç—å", "—à–µ–ª—É—à–µ–Ω–∏–µ", "–∂–∏—Ä–Ω–æ—Å—Ç—å", "–∞–∫–Ω–µ", "–ø–æ–∫—Ä–∞—Å–Ω–µ–Ω–∏–µ", "—Ä–∞–∑–¥—Ä–∞–∂–µ–Ω–∏–µ"], "key": "problems"}
]

# === –°–ª–æ–≤–∞—Ä—å –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –æ—Ç–≤–µ—Ç–æ–≤ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π ===
user_data = {}

# === –°—Ç–∞—Ä—Ç–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ ===
@bot.message_handler(commands=['start'])
def start(message):
    chat_id = message.chat.id
    user_data[chat_id] = {}
    bot.send_message(chat_id, "–ü—Ä–∏–≤–µ—Ç! –Ø YourSkincare, –ø–æ–º–æ–≥—É –ø–æ–¥–æ–±—Ä–∞—Ç—å —Å—Ä–µ–¥—Å—Ç–≤–∞ –¥–ª—è —Ç–≤–æ–µ–π –∫–æ–∂–∏ üåø")
    ask_question(chat_id, 0)

# === –§—É–Ω–∫—Ü–∏—è –∑–∞–¥–∞–µ—Ç –≤–æ–ø—Ä–æ—Å—ã –ø–æ –ø–æ—Ä—è–¥–∫—É ===
def ask_question(chat_id, q_index):
    if q_index < len(questions):
        q = questions[q_index]
        markup = types.ReplyKeyboardMarkup(one_time_keyboard=True, resize_keyboard=True)
        for option in q["options"]:
            markup.add(option)
        bot.send_message(chat_id, q["question"], reply_markup=markup)
        bot.register_next_step_handler_by_chat_id(chat_id, lambda msg: handle_answer(msg, q_index))
    else:
        # –í—Å–µ –≤–æ–ø—Ä–æ—Å—ã –æ—Ç–≤–µ—á–µ–Ω—ã, —Ñ–∏–ª—å—Ç—Ä—É–µ–º –ø—Ä–æ–¥—É–∫—Ç—ã
        send_filtered_products(chat_id)

def handle_answer(message, q_index):
    chat_id = message.chat.id
    answer = message.text.lower()
    key = questions[q_index]["key"]
    
    # –î–ª—è –ø—Ä–æ–±–ª–µ–º –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω–µ—Å–∫–æ–ª—å–∫–æ –æ—Ç–≤–µ—Ç–æ–≤ —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é
    if key == "problems":
        answer_list = [a.strip() for a in answer.split(",") if a.strip() in questions[q_index]["options"]]
        user_data[chat_id][key] = answer_list
    else:
        if answer in questions[q_index]["options"]:
            user_data[chat_id][key] = answer
        else:
            user_data[chat_id][key] = answer  # –Ω–∞ —Å–ª—É—á–∞–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –≤–≤–µ–ª —á—Ç–æ-—Ç–æ —Å–≤–æ–µ

    ask_question(chat_id, q_index + 1)

# === –§–∏–ª—å—Ç—Ä—É–µ–º –∏ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø—Ä–æ–¥—É–∫—Ç–æ–≤ ===
def send_filtered_products(chat_id):
    user_skin = user_data[chat_id].get("skin_type", "")
    user_problems = user_data[chat_id].get("problems", [])
    
    filtered = []
    for p in products:
        if user_skin in p["skin_type"] and any(prob in p["problems"] for prob in user_problems):
            filtered.append(p)
    
    if not filtered:
        bot.send_message(chat_id, "–ü–æ –≤–∞—à–∏–º –æ—Ç–≤–µ—Ç–∞–º –Ω–µ –Ω–∞–π–¥–µ–Ω–æ –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö —Å—Ä–µ–¥—Å—Ç–≤ üòî")
        return
    
    msg_text = "–ü–æ–¥—Ö–æ–¥—è—â–∏–µ —Å—Ä–µ–¥—Å—Ç–≤–∞ –¥–ª—è –≤–∞—Å:\n\n"
    for p in filtered:
        msg_text += f"‚Ä¢ {p['name']} ‚Äî {p['price']}\n  {p['description']}\n\n"
    
    bot.send_message(chat_id, msg_text, reply_markup=types.ReplyKeyboardRemove())

# === –ó–∞–ø—É—Å–∫ –±–æ—Ç–∞ ===
bot.infinity_polling()
